import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, TextInput, Button } from 'react-native';

export default function App() {
  const [lembrete, setLembrete] = useState('');
  const [lembretes, setLembretes] = useState([]);

  const capturaLembrete = (lembrete) => {
    setLembrete(lembrete);
  }

  const adicionarLembrete = () => {
    setLembretes(lembretes => {
      return [...lembretes, lembrete];
    });
  }

  return (
    <View style={styles.telaPrincipal}>
      <View>
        {/* cadastro lembretes */}
        <TextInput placeholder="Lembrar..." style={styles.lembreteTxt} onChangeText={capturaLembrete} value={lembrete}/>
        <Button title="Adicionar" onPress={adicionarLembrete}/>
      </View>

      <View>
        {/* lembretes */}
        {lembretes.map((lembrete) => <Text>{lembrete}</Text>)}
      </View>  
    </View>
  );
}

const styles = StyleSheet.create({
  telaPrincipal: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 50
  },
  lembreteTxt: {
    borderBottomColor: '#000', borderBottomWidth: 2, marginBottom: 4, textAlign: 'center'
  }
});
